	public boolean updateProductProperty(String columnName, Double value) {
		
		Connection conn = new MySQLDBConnector().getDBConnection();
		try {
			String qry = ""
				+ "UPDATE product"
				+ " SET " + columnName + " = ?"
				+ " WHERE id = " + this.id;
			PreparedStatement stmt = conn.prepareStatement(qry);
			stmt.setDouble(1, value);
			
			stmt.execute();			
			conn.close();
			return true;
			
		} catch (SQLException e) {
			try {
				conn.close();
			} catch (SQLException ex) {
				ex.printStackTrace();
			}
			e.printStackTrace();
			return false;
		}
		
	}
	

/*------------------------------------------------------------------------------------------------*/
/*-------------------------------------------[ DELETE ]-------------------------------------------*/
/*------------------------------------------------------------------------------------------------*/
/*------------------------------- Delete Product from a database. --------------------------------*/
/*------------------------------------------------------------------------------------------------*/

	/// Local ///
	/**
	 * <pre>
	 * Delete Product from a database.
	 *
	 * Usage:
	 *  Product product = new Product(id);
	 *  ...
	 *  
	 *  boolean result = product.deleteProduct();
	 * </pre>
	 * 
	 * @return True if deletion successful.
	 */
	public boolean deleteProduct() {
		
		return deleteProduct(this.id);
		
	}

	/// Static ///
	/**
	 * <pre>
	 * Delete Product from a database.
	 *
	 * Usage:
	 *  Product product = new Product();
	 *  ...
	 *  
	 *  boolean result = product.deleteProduct(id);
	 * </pre>
	 * 
	 * @param id Product id to be delete.
	 *
	 * @return  True if deletion successful.
	 */
	public static boolean deleteProduct(Integer id) {
		
		Connection conn = new MySQLDBConnector().getDBConnection();
		try {
			String qry = ""
				+ "DELETE FROM product"
				+ " WHERE id=?";
			PreparedStatement stmt = conn.prepareStatement(qry);
			stmt.setInt(1, id);
			
			stmt.execute();			
			conn.close();
			return true;
			
		} catch (SQLException e) {
			try {
				conn.close();
			} catch (SQLException ex) {
				ex.printStackTrace();
			}
			e.printStackTrace();
			return false;
		}
		
	}
	

/*------------------------------------------------------------------------------------------------*/
/*------------------------------------------[ IsEXIST ]-------------------------------------------*/
/*------------------------------------------------------------------------------------------------*/
/*------------- Check if record(s) from the given condition is exists in a database. -------------*/
/*------------------------------------------------------------------------------------------------*/

	/**
	 * <pre>
	 * Check if record(s) from the given condition is exists in a database.
	 *
	 * Usage:
	 *  Product product = Product.isExist(condition);
	 * </pre>
	 * 
	 * @param condition The condition to specify the record to be return.<pre>
	 *  Ex. "name = 'Foo' AND date BETWEEN '2021-01-01' AND '2021-12-31'"</pre>
	 * 
	 * @return The first Product object matches the given condition.
	 */
	public static Product isExist(String condition) {
		
		Connection conn = new MySQLDBConnector().getDBConnection();
		
		if(condition != "") condition = " WHERE " + condition;
		try {
			String qry = ""
				+ "SELECT *" 
				+ " FROM product"
				+ condition;
			Statement stmt = conn.createStatement();
			ResultSet rs = stmt.executeQuery(qry);
			while (rs.next()) {
				Product product = new Product(rs.getInt("id"), conn);
				conn.close();
				return product;
			}			
			conn.close();
			return null;
			
		} catch (SQLException e) {
			try {
				conn.close();
			} catch (SQLException ex) {
				ex.printStackTrace();
			}
			e.printStackTrace();
			return null;
		}

	}
	

/*------------------------------------------------------------------------------------------------*/
/*----------------------------------------[ ToCLASSINFO ]-----------------------------------------*/
/*------------------------------------------------------------------------------------------------*/
/*------------------------ Convert Product class to a ProductInfo class. -------------------------*/
/*------------------------------------------------------------------------------------------------*/

	/**
	 * <pre>
	 * Convert Product class to a ProductInfo class.
	 *
	 * Usage:
	 *  Product product = new Product();
	 *  ...
	 *  
	 *  ProductInfo productInfo = product.toProductInfo();
	 * </pre>
	 *
	 * @return ProductInfo object.
	 */
	public ProductInfo toProductInfo() {
        
		ProductInfo ci = new ProductInfo();
		ci.id = this.id;
		ci.category = this.category;
		ci.code = this.code;
		ci.name = this.name;
		ci.description = this.description;
		ci.cost = this.cost;
		ci.price = this.price;
		ci.image = this.image;
		ci.status = this.status;

		return ci;

	}

